function plot3nodes(p,varargin)%PLOT3NODES(P) plots the nodes specified by p (3xn).%%PLOT3NODES(P, LINESPEC) specifies how do draw the nodes,%   e.g., LINESPEC = 'r*'.%%PLOT3NODES(P, LINESPEC, FACECOLOR) adds a face color for the markers,%   e.g. PLOT3NODES(P, 'ko', 'w') plots solid white circles with black%   edges.%% The function leaves "hold on", so that subsequent plots% may be superimposed.% Default values for optional argument  linespec = 'b.'; % Handle optional arguments. Note that nargin is the *total* number of args. if (nargin > 1)    linespec = varargin{1};endnh = plot3(p(1,:), p(2,:), p(3,:), linespec);if (nargin > 2)    set(nh, 'markerfacecolor', varargin{2});endhold onaxis equalview(3)